mapper.update:
update automan.gift_card set gmt_modified=now()

    ,account_id=#{accountId}


    ,site_name=#{siteName}


    ,balance=#{balance}


    ,security_code=#{securityCode}


    ,is_used=#{isUsed}


    ,is_process=#{isProcess}


    ,is_suspect=#{isSuspect}


    ,real_balance=#{realBalance}


    ,is_recharge_now=#{isRechargeNow}

where id=#{id}
